import { HttpHeaders } from "@angular/common/http"; // marks a class as available to Injector for creation

const httpOptions = {
  headers: new HttpHeaders({
    "Content-Type": "application/json"
  })
};
export class DatabaseService {
  constructor(http) {
    this.http = http;
  }

  getActors() {
    return this.http.get("/actors");
  }

  getActor(id) {
    let url = "/actors/" + id;
    return this.http.get(url);
  }

  createActor(data) {
    return this.http.post("/actors", data, httpOptions);
  } // updateActor(id, data) {
  //   let url = "/actors/" + id;
  //   return this.http.put(url, data, httpOptions);
  // }


  deleteActor(id) {
    let url = "/actors/" + id;
    return this.http.delete(url, httpOptions);
  }

}
